import React from 'react';

const Bars = ({ array, comparing, swapping, sorted, speed }) => {
  const maxVal = Math.max(...array, 1);

  const getBarColor = (index) => {
    if (sorted && sorted.includes(index)) return 'bg-green-500';
    if (swapping && swapping.includes(index)) return 'bg-red-500';
    if (comparing && comparing.includes(index)) return 'bg-yellow-500';
    return 'bg-blue-500';
  };

  // Map speed (1..100) to CSS transition duration in ms (slower when lower speed value)
  const durationMs = Math.max(50, 800 - speed * 6); // matches App.jsx delay mapping

  return (
    <div className="flex flex-col items-center mt-8">
      <div className="flex justify-center items-end h-96 w-full bg-gray-200 dark:bg-gray-800 p-4 rounded-lg overflow-hidden">
        {array.map((value, idx) => (
          <div key={idx} className="flex-1 mx-0.5 flex flex-col items-center">
            <div
              className={`${getBarColor(idx)} w-full transition-all"`}
              style={{ height: `${(value / maxVal) * 100}%`, transitionDuration: `${durationMs}ms` }}
            ></div>
          </div>
        ))}
      </div>

      <div className="mt-2 w-full px-4">
        <div className="flex justify-between text-xs text-gray-700 dark:text-gray-300">
          {array.map((value, idx) => (
            <div key={idx} className="flex-1 text-center truncate px-0.5">{value}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Bars;
