import React, { useState, useEffect } from 'react';
import './SortingVisualizer.css';

const SortingVisualizer = () => {
  const [array, setArray] = useState([]);
  const [isAnimating, setIsAnimating] = useState(false);
  const [arraySize, setArraySize] = useState(30);

  // Generate random array
  const generateArray = () => {
    const newArray = [];
    for (let i = 0; i < arraySize; i++) {
      newArray.push(Math.floor(Math.random() * 300) + 20);
    }
    setArray(newArray);
    resetColors();
  };

  // Reset all bar colors
  const resetColors = () => {
    setTimeout(() => {
      const bars = document.querySelectorAll('.array-bar');
      bars.forEach(bar => {
        bar.style.backgroundColor = '#3498db';
      });
    }, 100);
  };

  // Initialize array on component mount
  useEffect(() => {
    generateArray();
  }, [arraySize]);

  // Sleep function for animation delays
  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  // Bubble Sort with animation
  const bubbleSort = async () => {
    if (isAnimating) return;
    setIsAnimating(true);
    
    const newArray = [...array];
    const n = newArray.length;

    for (let i = 0; i < n - 1; i++) {
      for (let j = 0; j < n - i - 1; j++) {
        // Highlight current comparison
        const bars = document.querySelectorAll('.array-bar');
        if (bars[j]) bars[j].style.backgroundColor = 'red';
        if (bars[j + 1]) bars[j + 1].style.backgroundColor = 'red';
        
        await sleep(100);

        // Swap if needed
        if (newArray[j] > newArray[j + 1]) {
          [newArray[j], newArray[j + 1]] = [newArray[j + 1], newArray[j]];
          setArray([...newArray]);
          
          // Show swap in green
          if (bars[j]) bars[j].style.backgroundColor = 'green';
          if (bars[j + 1]) bars[j + 1].style.backgroundColor = 'green';
          await sleep(100);
        }

        // Reset colors
        if (bars[j]) bars[j].style.backgroundColor = '#3498db';
        if (bars[j + 1]) bars[j + 1].style.backgroundColor = '#3498db';
      }
    }

    // Mark as completed
    const bars = document.querySelectorAll('.array-bar');
    bars.forEach(bar => {
      bar.style.backgroundColor = 'green';
    });

    setIsAnimating(false);
  };

  // Selection Sort with animation
  const selectionSort = async () => {
    if (isAnimating) return;
    setIsAnimating(true);
    
    const newArray = [...array];
    const n = newArray.length;

    for (let i = 0; i < n - 1; i++) {
      let minIdx = i;
      const bars = document.querySelectorAll('.array-bar');
      
      // Mark current position
      if (bars[i]) bars[i].style.backgroundColor = 'yellow';

      for (let j = i + 1; j < n; j++) {
        // Highlight comparison
        if (bars[j]) bars[j].style.backgroundColor = 'red';
        await sleep(50);

        if (newArray[j] < newArray[minIdx]) {
          // Reset previous minimum
          if (bars[minIdx] && minIdx !== i) bars[minIdx].style.backgroundColor = '#3498db';
          minIdx = j;
          if (bars[minIdx]) bars[minIdx].style.backgroundColor = 'orange';
        } else {
          if (bars[j]) bars[j].style.backgroundColor = '#3498db';
        }
      }

      // Swap elements
      if (minIdx !== i) {
        [newArray[i], newArray[minIdx]] = [newArray[minIdx], newArray[i]];
        setArray([...newArray]);
        
        // Show swap
        if (bars[i]) bars[i].style.backgroundColor = 'green';
        if (bars[minIdx]) bars[minIdx].style.backgroundColor = 'green';
        await sleep(200);
      }

      // Mark as sorted
      if (bars[i]) bars[i].style.backgroundColor = 'purple';
    }

    // Mark last element as sorted
    const bars = document.querySelectorAll('.array-bar');
    if (bars[n-1]) bars[n-1].style.backgroundColor = 'purple';

    setIsAnimating(false);
  };

  // Quick Sort wrapper
  const quickSort = async () => {
    if (isAnimating) return;
    setIsAnimating(true);
    
    const newArray = [...array];
    await quickSortHelper(newArray, 0, newArray.length - 1);
    
    // Mark all as completed
    const bars = document.querySelectorAll('.array-bar');
    bars.forEach(bar => {
      bar.style.backgroundColor = 'green';
    });

    setIsAnimating(false);
  };

  const quickSortHelper = async (arr, low, high) => {
    if (low < high) {
      const pi = await partition(arr, low, high);
      await quickSortHelper(arr, low, pi - 1);
      await quickSortHelper(arr, pi + 1, high);
    }
  };

  const partition = async (arr, low, high) => {
    const pivot = arr[high];
    let i = low - 1;
    const bars = document.querySelectorAll('.array-bar');

    // Mark pivot
    if (bars[high]) bars[high].style.backgroundColor = 'yellow';

    for (let j = low; j < high; j++) {
      if (bars[j]) bars[j].style.backgroundColor = 'red';
      await sleep(100);

      if (arr[j] < pivot) {
        i++;
        [arr[i], arr[j]] = [arr[j], arr[i]];
        setArray([...arr]);
        
        if (bars[i]) bars[i].style.backgroundColor = 'green';
        await sleep(100);
      }
      
      if (bars[j]) bars[j].style.backgroundColor = '#3498db';
    }

    [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]];
    setArray([...arr]);
    
    if (bars[high]) bars[high].style.backgroundColor = '#3498db';
    return i + 1;
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      {/* Controls */}
      <div style={{ marginBottom: '30px' }}>
        <button 
          onClick={generateArray} 
          disabled={isAnimating}
          style={{
            padding: '10px 20px',
            margin: '0 5px',
            backgroundColor: isAnimating ? '#ccc' : '#3498db',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: isAnimating ? 'not-allowed' : 'pointer',
            fontSize: '16px'
          }}
        >
          Generate New Array
        </button>
        
        <button 
          onClick={bubbleSort} 
          disabled={isAnimating}
          style={{
            padding: '10px 20px',
            margin: '0 5px',
            backgroundColor: isAnimating ? '#ccc' : '#e74c3c',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: isAnimating ? 'not-allowed' : 'pointer',
            fontSize: '16px'
          }}
        >
          Bubble Sort
        </button>
        
        <button 
          onClick={selectionSort} 
          disabled={isAnimating}
          style={{
            padding: '10px 20px',
            margin: '0 5px',
            backgroundColor: isAnimating ? '#ccc' : '#f39c12',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: isAnimating ? 'not-allowed' : 'pointer',
            fontSize: '16px'
          }}
        >
          Selection Sort
        </button>
        
        <button 
          onClick={quickSort} 
          disabled={isAnimating}
          style={{
            padding: '10px 20px',
            margin: '0 5px',
            backgroundColor: isAnimating ? '#ccc' : '#27ae60',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: isAnimating ? 'not-allowed' : 'pointer',
            fontSize: '16px'
          }}
        >
          Quick Sort
        </button>
      </div>

      {/* Array Size Slider */}
      <div style={{ marginBottom: '20px' }}>
        <label style={{ fontSize: '16px', fontWeight: 'bold' }}>
          Array Size: {arraySize}
          <br />
          <input
            type="range"
            min="10"
            max="50"
            value={arraySize}
            onChange={(e) => setArraySize(Number(e.target.value))}
            disabled={isAnimating}
            style={{ marginTop: '5px', width: '200px' }}
          />
        </label>
      </div>

      {/* Array Visualization */}
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-end',
        height: '400px',
        padding: '20px',
        backgroundColor: '#f8f9fa',
        borderRadius: '10px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
      }}>
        {array.map((value, idx) => (
          <div
            key={idx}
            className="array-bar"
            style={{
              height: `${value}px`,
              width: `${Math.max(8, 600 / array.length)}px`,
              backgroundColor: '#3498db',
              margin: '0 1px',
              borderRadius: '3px 3px 0 0',
              transition: 'background-color 0.3s ease'
            }}
          />
        ))}
      </div>

      {/* Status */}
      <div style={{ marginTop: '20px', fontSize: '18px', fontWeight: 'bold' }}>
        {isAnimating ? 'Sorting in progress...' : 'Ready to sort!'}
      </div>
    </div>
  );
};

export default SortingVisualizer;
