import React from 'react';

const algoDetails = {
  bubble: {
    name: 'Bubble Sort',
    complexity: 'O(n^2)',
    space: 'O(1)',
  },
  insertion: {
    name: 'Insertion Sort',
    complexity: 'O(n^2)',
    space: 'O(1)',
  },
  merge: {
    name: 'Merge Sort',
    complexity: 'O(n log n)',
    space: 'O(n)',
  },
  quick: {
    name: 'Quick Sort',
    complexity: 'O(n log n) average, O(n^2) worst',
    space: 'O(log n)',
  },
  heap: {
    name: 'Heap Sort',
    complexity: 'O(n log n)',
    space: 'O(1)',
  },
};

const InfoPanel = ({ algorithm }) => {
  const details = algoDetails[algorithm];

  return (
    <div className="mt-8 p-4 bg-white dark:bg-gray-800 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-2">{details.name}</h2>
      <p><strong>Time Complexity:</strong> {details.complexity}</p>
      <p><strong>Space Complexity:</strong> {details.space}</p>
    </div>
  );
};

export default InfoPanel;
