import React, { useState } from 'react';

const Controls = ({ onGenerate, onSort, onAlgorithmChange, onSizeChange, onSpeedChange, onCustomArraySubmit, isSorting, algorithm, size, speed }) => {
  const algorithms = ['bubble', 'insertion', 'merge', 'quick', 'heap'];
  const [customArrayInput, setCustomArrayInput] = useState('');

  const handleCustomSubmit = () => {
    const parsedArray = customArrayInput
      .split(',')
      .map(item => parseInt(item.trim(), 10))
      .filter(num => !isNaN(num));

    if (parsedArray.length > 0) {
      onCustomArraySubmit(parsedArray);
    } else {
      alert('Please enter a valid comma-separated list of numbers.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center gap-4 p-4 bg-white dark:bg-gray-800 rounded-lg shadow-md">
      <div className="flex flex-wrap items-center justify-center gap-4">
        <button onClick={onGenerate} disabled={isSorting} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400">
          Generate New Array
        </button>
        
        <select onChange={(e) => onAlgorithmChange(e.target.value)} value={algorithm} disabled={isSorting} className="px-4 py-2 border rounded dark:bg-gray-700 dark:border-gray-600">
          {algorithms.map(alg => <option key={alg} value={alg}>{alg.charAt(0).toUpperCase() + alg.slice(1)} Sort</option>)}
        </select>

        <div className="flex items-center gap-2">
          <label htmlFor="size">Size:</label>
          <input type="range" id="size" min="10" max="100" value={size} onChange={(e) => onSizeChange(Number(e.target.value))} disabled={isSorting} />
        </div>

        <div className="flex items-center gap-2">
          <label htmlFor="speed">Speed:</label>
          <input type="range" id="speed" min="1" max="100" value={speed} onChange={(e) => onSpeedChange(Number(e.target.value))} disabled={isSorting} />
        </div>

        <button onClick={onSort} disabled={isSorting} className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 disabled:bg-gray-400">
          Sort!
        </button>
      </div>
      <div className="flex items-center justify-center gap-2 mt-4 w-full max-w-md">
        <input
          type="text"
          placeholder="Enter comma-separated numbers (e.g., 5,3,8,1)"
          value={customArrayInput}
          onChange={(e) => setCustomArrayInput(e.target.value)}
          disabled={isSorting}
          className="w-full px-4 py-2 border rounded dark:bg-gray-700 dark:border-gray-600"
        />
        <button onClick={handleCustomSubmit} disabled={isSorting} className="px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700 disabled:bg-gray-400">
          Set Array
        </button>
      </div>
    </div>
  );
};

export default Controls;
