import React from 'react';

const Bars = ({ array, comparing, swapping, sorted }) => {
  const getBarColor = (index) => {
    if (sorted.includes(index)) return 'bg-green-500';
    if (swapping.includes(index)) return 'bg-red-500';
    if (comparing.includes(index)) return 'bg-yellow-500';
    return 'bg-blue-500';
  };

  return (
    <div className="flex justify-center items-end h-96 mt-8 bg-gray-200 dark:bg-gray-800 p-4 rounded-lg">
      {array.map((value, idx) => (
        <div
          key={idx}
          className={`w-full mx-px transition-all duration-100 ${getBarColor(idx)}`}
          style={{ height: `${(value / Math.max(...array, 1)) * 100}%` }}
        ></div>
      ))}
    </div>
  );
};

export default Bars;
