import React, { useState, useEffect } from 'react';

function App() {
  const [array, setArray] = useState([]);
  const [isAnimating, setIsAnimating] = useState(false);
  const [arraySize, setArraySize] = useState(25);

  const generateArray = () => {
    const newArray = [];
    for (let i = 0; i < arraySize; i++) {
      newArray.push(Math.floor(Math.random() * 300) + 20);
    }
    setArray(newArray);
    resetColors();
  };

  const resetColors = () => {
    setTimeout(() => {
      const bars = document.querySelectorAll('.bar');
      bars.forEach(bar => {
        bar.style.backgroundColor = '#3498db';
      });
    }, 100);
  };

  useEffect(() => {
    generateArray();
  }, [arraySize]);

  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  const bubbleSort = async () => {
    if (isAnimating) return;
    setIsAnimating(true);
    
    const newArray = [...array];
    const bars = document.querySelectorAll('.bar');

    for (let i = 0; i < newArray.length - 1; i++) {
      for (let j = 0; j < newArray.length - i - 1; j++) {
        // Highlight comparison
        if (bars[j]) bars[j].style.backgroundColor = 'red';
        if (bars[j + 1]) bars[j + 1].style.backgroundColor = 'red';
        
        await sleep(100);

        if (newArray[j] > newArray[j + 1]) {
          // Swap
          [newArray[j], newArray[j + 1]] = [newArray[j + 1], newArray[j]];
          setArray([...newArray]);
          
          if (bars[j]) bars[j].style.backgroundColor = 'green';
          if (bars[j + 1]) bars[j + 1].style.backgroundColor = 'green';
          await sleep(100);
        }

        // Reset colors
        if (bars[j]) bars[j].style.backgroundColor = '#3498db';
        if (bars[j + 1]) bars[j + 1].style.backgroundColor = '#3498db';
      }
    }

    // Mark all as sorted
    bars.forEach(bar => {
      if (bar) bar.style.backgroundColor = 'green';
    });

    setIsAnimating(false);
  };

  const selectionSort = async () => {
    if (isAnimating) return;
    setIsAnimating(true);
    
    const newArray = [...array];
    const bars = document.querySelectorAll('.bar');

    for (let i = 0; i < newArray.length - 1; i++) {
      let minIdx = i;
      
      if (bars[i]) bars[i].style.backgroundColor = 'yellow';

      for (let j = i + 1; j < newArray.length; j++) {
        if (bars[j]) bars[j].style.backgroundColor = 'red';
        await sleep(50);

        if (newArray[j] < newArray[minIdx]) {
          if (bars[minIdx] && minIdx !== i) bars[minIdx].style.backgroundColor = '#3498db';
          minIdx = j;
          if (bars[minIdx]) bars[minIdx].style.backgroundColor = 'orange';
        } else {
          if (bars[j]) bars[j].style.backgroundColor = '#3498db';
        }
      }

      if (minIdx !== i) {
        [newArray[i], newArray[minIdx]] = [newArray[minIdx], newArray[i]];
        setArray([...newArray]);
      }

      if (bars[i]) bars[i].style.backgroundColor = 'green';
      if (bars[minIdx]) bars[minIdx].style.backgroundColor = '#3498db';
      await sleep(200);
    }

    if (bars[newArray.length - 1]) bars[newArray.length - 1].style.backgroundColor = 'green';
    setIsAnimating(false);
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h1 style={{ 
        textAlign: 'center', 
        color: 'white', 
        marginBottom: '30px',
        fontSize: '2.5rem',
        textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
      }}>
        🔢 Sorting Algorithm Visualizer
      </h1>
      
      <div style={{ 
        backgroundColor: 'rgba(255,255,255,0.1)', 
        borderRadius: '20px', 
        padding: '30px',
        backdropFilter: 'blur(10px)',
        boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
        border: '1px solid rgba(255,255,255,0.2)'
      }}>
        {/* Controls */}
        <div style={{ 
          textAlign: 'center', 
          marginBottom: '30px',
          display: 'flex',
          justifyContent: 'center',
          gap: '15px',
          flexWrap: 'wrap'
        }}>
          <button 
            onClick={generateArray} 
            disabled={isAnimating}
            style={{
              padding: '15px 25px',
              backgroundColor: isAnimating ? '#95a5a6' : '#e74c3c',
              color: 'white',
              border: 'none',
              borderRadius: '10px',
              cursor: isAnimating ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
              transform: isAnimating ? 'none' : 'translateY(0)',
            }}
            onMouseOver={(e) => {
              if (!isAnimating) {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 7px 20px rgba(0,0,0,0.4)';
              }
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 5px 15px rgba(0,0,0,0.3)';
            }}
          >
            🔄 Generate Array
          </button>
          
          <button 
            onClick={bubbleSort} 
            disabled={isAnimating}
            style={{
              padding: '15px 25px',
              backgroundColor: isAnimating ? '#95a5a6' : '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '10px',
              cursor: isAnimating ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              boxShadow: '0 5px 15px rgba(0,0,0,0.3)'
            }}
            onMouseOver={(e) => {
              if (!isAnimating) {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 7px 20px rgba(0,0,0,0.4)';
              }
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 5px 15px rgba(0,0,0,0.3)';
            }}
          >
            🫧 Bubble Sort
          </button>
          
          <button 
            onClick={selectionSort} 
            disabled={isAnimating}
            style={{
              padding: '15px 25px',
              backgroundColor: isAnimating ? '#95a5a6' : '#f39c12',
              color: 'white',
              border: 'none',
              borderRadius: '10px',
              cursor: isAnimating ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              boxShadow: '0 5px 15px rgba(0,0,0,0.3)'
            }}
            onMouseOver={(e) => {
              if (!isAnimating) {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 7px 20px rgba(0,0,0,0.4)';
              }
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 5px 15px rgba(0,0,0,0.3)';
            }}
          >
            🎯 Selection Sort
          </button>
        </div>

        {/* Array Size Control */}
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <label style={{ 
            color: 'white', 
            fontSize: '18px', 
            fontWeight: 'bold',
            display: 'block'
          }}>
            📏 Array Size: {arraySize}
            <br />
            <input
              type="range"
              min="10"
              max="50"
              value={arraySize}
              onChange={(e) => setArraySize(Number(e.target.value))}
              disabled={isAnimating}
              style={{ 
                marginTop: '15px', 
                width: '300px',
                height: '8px',
                background: 'linear-gradient(to right, #3498db, #e74c3c)',
                borderRadius: '5px',
                outline: 'none',
                cursor: isAnimating ? 'not-allowed' : 'pointer'
              }}
            />
          </label>
        </div>

        {/* Array Visualization */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'flex-end',
          height: '400px',
          padding: '20px',
          backgroundColor: 'rgba(255,255,255,0.95)',
          borderRadius: '15px',
          boxShadow: 'inset 0 4px 20px rgba(0,0,0,0.1)',
          gap: '2px',
          overflow: 'hidden'
        }}>
          {array.map((value, idx) => (
            <div
              key={`${idx}-${value}`}
              className="bar"
              style={{
                height: `${value}px`,
                width: `${Math.max(8, 600 / array.length)}px`,
                backgroundColor: '#3498db',
                borderRadius: '4px 4px 0 0',
                transition: 'all 0.3s ease',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
                border: '1px solid rgba(255,255,255,0.3)'
              }}
            />
          ))}
        </div>

        {/* Status */}
        <div style={{ 
          textAlign: 'center', 
          marginTop: '25px', 
          color: 'white',
          fontSize: '20px',
          fontWeight: 'bold',
          padding: '15px',
          backgroundColor: 'rgba(255,255,255,0.1)',
          borderRadius: '10px',
          border: '1px solid rgba(255,255,255,0.2)'
        }}>
          {isAnimating ? '🔄 Sorting in progress...' : '✅ Ready to sort!'}
        </div>

        {/* Instructions */}
        <div style={{
          marginTop: '20px',
          color: 'rgba(255,255,255,0.8)',
          fontSize: '14px',
          textAlign: 'center',
          lineHeight: '1.6'
        }}>
          <p>🔴 Red: Comparing elements | 🟢 Green: Swapping/Sorted | 🟡 Yellow: Current minimum | 🟠 Orange: New minimum</p>
        </div>
      </div>
    </div>
  );
}

export default App;
