import React from 'react';

const Bars = ({ array }) => {
  // guard against empty array
  const maxVal = array && array.length ? Math.max(...array) : 1;

  return (
    <div className="flex flex-col items-center mt-8 w-full">
      <div className="flex justify-center items-end h-96 w-full bg-gray-200 dark:bg-gray-800 p-4 rounded-lg overflow-hidden">
        {array.map((value, idx) => (
          <div key={idx} className="flex-1 h-full flex flex-col justify-end items-center min-w-[4px] mx-0.5">
            <div
              className="bg-blue-500 w-full"
              style={{ height: `${(value / maxVal) * 100}%` }}
            ></div>
          </div>
        ))}
      </div>

      <div className="mt-2 w-full px-4">
        <div className="flex justify-around text-xs text-gray-700 dark:text-gray-300">
          {array.map((value, idx) => (
            <div key={idx} className="flex-1 text-center truncate px-0.5">{value}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Bars;
