import React, { useState } from 'react';

const Controls = ({
  onGenerate,
  onSort,
  onStop,
  onTogglePause,
  onAlgorithmChange,
  onSizeChange,
  onSpeedChange,
  onCustomArraySubmit,
  isSorting,
  isPaused,
  algorithm,
  size,
  speed,
}) => {
  const algorithms = ['bubble', 'insertion', 'merge', 'quick', 'heap'];
  const [customArrayInput, setCustomArrayInput] = useState('');

  const handleCustomSubmit = () => {
    const parsedArray = customArrayInput
      .split(',')
      .map(item => parseInt(item.trim(), 10))
      .filter(num => !isNaN(num));

    if (parsedArray.length > 0) {
      onCustomArraySubmit(parsedArray);
    } else {
      alert('Please enter a valid comma-separated list of numbers.');
    }
  };

  return (
    <div className="p-4 bg-white dark:bg-gray-800 rounded-lg shadow-lg">
      {/* Top Row: Main Controls */}
      <div className="flex flex-wrap items-center justify-center gap-4 mb-4">
        <button onClick={onGenerate} disabled={isSorting} className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:bg-gray-400 transition-colors">
          <span role="img" aria-label="refresh">üîÑ</span>
          New Array
        </button>
        
        <select onChange={(e) => onAlgorithmChange(e.target.value)} value={algorithm} disabled={isSorting} className="px-4 py-2 border rounded-lg dark:bg-gray-700 dark:border-gray-600">
          {algorithms.map(alg => <option key={alg} value={alg}>{alg.charAt(0).toUpperCase() + alg.slice(1)} Sort</option>)}
        </select>

        {!isSorting ? (
          <button onClick={onSort} className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors">
            <span role="img" aria-label="play">‚ñ∂Ô∏è</span>
            Sort
          </button>
        ) : (
          <>
            <button onClick={onTogglePause} className="flex items-center gap-2 px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 transition-colors">
              {isPaused ? <span role="img" aria-label="play">‚ñ∂Ô∏è</span> : <span role="img" aria-label="pause">‚è∏Ô∏è</span>}
              {isPaused ? 'Resume' : 'Pause'}
            </button>
            <button onClick={onStop} className="flex items-center gap-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors">
              <span role="img" aria-label="stop">‚èπÔ∏è</span>
              Stop
            </button>
          </>
        )}
      </div>

      {/* Bottom Row: Sliders and Custom Input */}
      <div className="flex flex-wrap items-center justify-center gap-6">
        <div className="flex items-center gap-2">
          <label htmlFor="size" className="font-medium">Size</label>
          <input type="range" id="size" min="10" max="100" value={size} onChange={(e) => onSizeChange(Number(e.target.value))} disabled={isSorting} className="w-32" />
        </div>

        <div className="flex items-center gap-2">
          <label htmlFor="speed" className="font-medium">Speed</label>
          <input type="range" id="speed" min="1" max="100" value={speed} onChange={(e) => onSpeedChange(Number(e.target.value))} className="w-32" />
        </div>
      </div>
      
      <div className="flex items-center justify-center gap-2 mt-4 w-full max-w-lg mx-auto">
        <input
          type="text"
          placeholder="Or enter a custom array: 5, 3, 8, 1"
          value={customArrayInput}
          onChange={(e) => setCustomArrayInput(e.target.value)}
          disabled={isSorting}
          className="w-full px-4 py-2 border rounded-lg dark:bg-gray-700 dark:border-gray-600"
        />
        <button onClick={handleCustomSubmit} disabled={isSorting} className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 disabled:bg-gray-400 transition-colors">
          Set
        </button>
      </div>
    </div>
  );
};

export default Controls;
